mapper.select:
SELECT
FROM (SELECT DISTINCT RTD.REACH_NAME, (SELECT ( CASE WHEN NWIS_SITE_NO IS NOT NULL THEN NWIS_SITE_NO ELSE SHORT_NAME END) SITE_UP FROM SITE_STAR S WHERE S.SITE_ID = RTD.SITE_ID_UP ) SITE_UP, (SELECT S.NAME FROM SITE_STAR S WHERE S.SITE_ID = RTD.SITE_ID_UP ) DISPLAY_NAME_UP, (SELECT ( CASE WHEN NWIS_SITE_NO IS NOT NULL THEN NWIS_SITE_NO ELSE SHORT_NAME END) FROM SITE_STAR S WHERE S.SITE_ID = RTD.SITE_ID_DOWN ) SITE_DOWN, (SELECT S.NAME FROM SITE_STAR S WHERE S.SITE_ID = RTD.SITE_ID_DOWN ) DISPLAY_NAME_DOWN, (SELECT ( CASE WHEN NWIS_SITE_NO IS NOT NULL THEN NWIS_SITE_NO ELSE SHORT_NAME END) FROM SITE_STAR S WHERE S.SITE_ID = RTD.DISCHARGE_DOWN ) DISCHARGE_SITE_DOWN, (SELECT S.NAME FROM SITE_STAR S WHERE S.SITE_ID = RTD.DISCHARGE_DOWN ) DISCHARGE_DISPLAY_NAME_DOWN, CASE WHEN RTD.NETWORK_NAME='GCDAMP' THEN 'GCDAMP' WHEN RTD.NETWORK_NAME='Dinosaur' THEN 'DINO' WHEN RTD.NETWORK_NAME='BigBend' THEN 'BIBE' WHEN RTD.NETWORK_NAME='Canyonlands' THEN 'CL' WHEN RTD.NETWORK_NAME='RiverDelta' THEN 'CRD' ELSE RTD.NETWORK_NAME END AS NETWORK_NAME, RTD.DISPLAY_ORDER, TO_CHAR(RTD.END_STATIC_REC, 'YYYY-MM-DD"T"HH24:MI:SS') END_STATIC_REC, TO_CHAR(RTD.NEWEST_SUSPSED, 'YYYY-MM-DD"T"HH24:MI:SS') NEWEST_SUSPSED FROM REACH_DISPLAY RTD) T_A_REACH
AND NETWORK_NAME = #{network}
AND SITE_UP = #{upstream}
AND SITE_DOWN = #{downstream}
ORDER BY DISPLAY_ORDER ASC
