mapper.select:
SELECT
FROM (SELECT DISTINCT RTD.REACH_NAME, (SELECT ( CASE WHEN NWIS_SITE_NO IS NOT NULL THEN NWIS_SITE_NO ELSE SHORT_NAME END) SITE_UP FROM SITE_STAR S WHERE S.SITE_ID = RTD.SITE_ID_UP ) SITE_UP, (SELECT S.NAME FROM SITE_STAR S WHERE S.SITE_ID = RTD.SITE_ID_UP ) DISPLAY_NAME_UP, (SELECT ( CASE WHEN NWIS_SITE_NO IS NOT NULL THEN NWIS_SITE_NO ELSE SHORT_NAME END) SITE_UP_SECONDARY FROM SITE_STAR S WHERE S.SITE_ID = RTD.SITE_ID_UP_SECONDARY ) SITE_UP_SECONDARY, (SELECT S.NAME FROM SITE_STAR S WHERE S.SITE_ID = RTD.SITE_ID_UP_SECONDARY ) DISPLAY_NAME_UP_SECONDARY, (SELECT ( CASE WHEN NWIS_SITE_NO IS NOT NULL THEN NWIS_SITE_NO ELSE SHORT_NAME END) FROM SITE_STAR S WHERE S.SITE_ID = RTD.SITE_ID_DOWN ) SITE_DOWN, (SELECT S.NAME FROM SITE_STAR S WHERE S.SITE_ID = RTD.SITE_ID_DOWN ) DISPLAY_NAME_DOWN, (SELECT ( CASE WHEN NWIS_SITE_NO IS NOT NULL THEN NWIS_SITE_NO ELSE SHORT_NAME END) FROM SITE_STAR S WHERE S.SITE_ID = RTD.DISCHARGE_DOWN ) DISCHARGE_SITE_DOWN, (SELECT S.NAME FROM SITE_STAR S WHERE S.SITE_ID = RTD.DISCHARGE_DOWN ) DISCHARGE_DISPLAY_NAME_DOWN, CASE WHEN RTD.NETWORK_NAME='GCDAMP' THEN 'GCDAMP' WHEN RTD.NETWORK_NAME='Dinosaur' THEN 'DINO' WHEN RTD.NETWORK_NAME='BigBend' THEN 'BIBE' WHEN RTD.NETWORK_NAME='Canyonlands' THEN 'CL' WHEN RTD.NETWORK_NAME='RiverDelta' THEN 'CRD' ELSE RTD.NETWORK_NAME END AS NETWORK_NAME, RTD.DISPLAY_ORDER, TO_CHAR(RTD.END_STATIC_REC, 'YYYY-MM-DD"T"HH24:MI:SS') END_STATIC_REC, TO_CHAR(RTD.NEWEST_SUSPSED, 'YYYY-MM-DD"T"HH24:MI:SS') NEWEST_SUSPSED, (SELECT S.RIVER_NAME FROM SITE_STAR S WHERE RTD.MAJOR_SITE = S.SITE_ID ) MAJOR_TRIB_RIVER FROM REACH_DISPLAY RTD) T_A_REACH
AND NETWORK_NAME = #{network}
AND SITE_UP = #{upstream}
AND SITE_DOWN = #{downstream}
ORDER BY DISPLAY_ORDER ASC

SELECT
from (select distinct rtd.reach_name, (select (case when nwis_site_no is not null then nwis_site_no else short_name end) site_up from site_star s where s.site_id = rtd.site_id_up) site_up, (select s.name from site_star s where s.site_id = rtd.site_id_up) display_name_up, (select (case when nwis_site_no is not null then nwis_site_no else short_name end) from site_star s where s.site_id = rtd.site_id_down) site_down, (select s.name from site_star s where s.site_id = rtd.site_id_down) display_name_down, (select (case when nwis_site_no is not null then nwis_site_no else short_name end) from site_star s where s.site_id = rtd.discharge_down) discharge_site_down, (select s.name from site_star s where s.site_id = rtd.discharge_down) discharge_display_name_down, (select s.river_name from site_star s where rtd.major_site = s.site_id) as major_trib_river, (select (case when nwis_site_no is not null then nwis_site_no else short_name end) from site_star s where s.site_id = rtd.major_site) major_trib_site_name, (select name from group_name where rtd.major_group = group_name.group_id) major_group, (select (case when nwis_site_no is not null then nwis_site_no else short_name end) from site_star s where s.site_id = rtd.minor_site) minor_trib_site_name, (select name from group_name where group_name.group_id = rtd.minor_group) minor_group, case when rtd.network_name = 'GCDAMP' then 'GCDAMP' when rtd.network_name = 'Dinosaur' then 'DINO' when rtd.network_name = 'BigBend' then 'BIBE' when rtd.network_name = 'Canyonlands' then 'CL' when rtd.network_name = 'RiverDelta' then 'CRD' else rtd.network_name end as network_name, rtd.display_order, to_char (end_static_rec, 'YYYY-MM-DD"T"HH24:MI:SS') end_static_rec, to_char (newest_suspsed, 'YYYY-MM-DD"T"HH24:MI:SS') newest_suspsed from reach_display rtd) t_a_reaches
major_trib_site_name = #{majorTribSite}

select distinct name group_name from group_name, reach_display rtd 
	where group_name.group_id = rtd.reach_group
	and rtd.reach_name = #{reach_name,jdbcType=VARCHAR}

SELECT
FROM (SELECT DISTINCT (SELECT (CASE WHEN NWIS_SITE_NO IS NOT NULL THEN NWIS_SITE_NO ELSE SHORT_NAME END) FROM SITE_STAR WHERE SITE_STAR.SITE_ID = SITE_ID_UP) UPSTREAM_STATION, (SELECT NAME FROM GROUP_NAME WHERE GROUP_NAME.GROUP_ID = RTD.REACH_GROUP) REACH_GROUP, (SELECT NAME FROM GROUP_NAME WHERE GROUP_NAME.GROUP_ID = RTD.MAJOR_GROUP) MAJOR_GROUP, (SELECT ( CASE WHEN NWIS_SITE_NO IS NOT NULL THEN NWIS_SITE_NO ELSE SHORT_NAME END) SITE_UP FROM SITE_STAR S WHERE S.SITE_ID = RTD.MAJOR_SITE ) MAJOR_SITE, (SELECT NAME FROM GROUP_NAME WHERE GROUP_NAME.GROUP_ID = RTD.MINOR_GROUP) MINOR_GROUP, (SELECT ( CASE WHEN NWIS_SITE_NO IS NOT NULL THEN NWIS_SITE_NO ELSE SHORT_NAME END) SITE_UP FROM SITE_STAR S WHERE S.SITE_ID = RTD.MINOR_SITE ) MINOR_SITE FROM REACH_DISPLAY RTD) T_A_INNER
AND UPSTREAM_STATION = #{upstream}
